import React from "react";

import ChartBar from './ChartBar';
import "./Chart.css";

const Chart = props => {
    const dataPointValues = props.dataPointValues.map(dataPointValues => dataPointValues.value);
    const totalMaximum = Math.max(...dataPointValues);

    return (
        <div className="chart">
            {props.dataPoints.map((dataPoint) => (
                <ChartBar
                    key={dataPoint.label}
                    value={dataPoint.vakue}
                    maxValue={totalMaximum}
                    label={dataPoint.label}
                />
            ))}
        </div>
    )
};

export default Chart;
